CREATE VIEW `vtaxonidentity` AS select `t`.`taxonId` AS `taxonId`,`t`.`genusId` AS `genusId`,`g`.`genusName` AS `genusName`,`t`.`speciesName` AS `speciesName`,`t`.`hybridName` AS `hybridName`,(case when (`t`.`isSystemManaged` = 1) then concat(`g`.`genusName`,' sp.') when ((`t`.`speciesName` is null) and (`t`.`hybridName` is null)) then `g`.`genusName` when (`t`.`speciesName` is not null) then concat(`g`.`genusName`,' ',`t`.`speciesName`) else concat(`g`.`genusName`,' ',`t`.`hybridName`) end) AS `displayName`,`t`.`taxonNotes` AS `taxonNotes`,`t`.`isActive` AS `isActive` from (`taxon` `t` join `genus` `g` on((`g`.`genusId` = `t`.`genusId`))) where (`t`.`isActive` = 1);

